import React, { useState, useEffect } from 'react';
import CombinedHistogram from './CombinedHistogram.tsx';
import './components/HedgingSimulator.css';

function HedgingSimulator() {
  const [months, setMonths] = useState(6);
  const [exposure, setExposure] = useState(1000000);
  const [numSimulations, setNumSimulations] = useState(10000);
  const [jsonData, setJsonData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingDots, setLoadingDots] = useState('');

  // Add loading dots animation
  useEffect(() => {
    if (loading) {
      const interval = setInterval(() => {
        setLoadingDots(dots => dots.length >= 3 ? '' : dots + '.');
      }, 500);
      return () => clearInterval(interval);
    } else {
      setLoadingDots('');
    }
  }, [loading]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setJsonData(null);
    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/api/fetch', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          months: months,
          exposure: exposure,
          num_simulations: numSimulations,
        }),
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setJsonData(data);
    } catch (err) {
      console.error('Error fetching simulation data:', err);
      setError('Failed to fetch data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="simulator-content">
      <div className="simulator-container">
        <h2 className="simulator-title">Configure Simulation</h2>
        <form onSubmit={handleSubmit} className="simulator-form">
          <div className="form-group">
            <label>
              Time Horizon
              <select
                value={months}
                onChange={(e) => setMonths(parseInt(e.target.value, 10))}
                className="form-control"
              >
                <option value={3}>3 Months</option>
                <option value={6}>6 Months</option>
                <option value={12}>12 Months</option>
              </select>
            </label>
          </div>
          <div className="form-group">
            <label>
              Exposure Amount
              <input
                type="number"
                value={exposure}
                onChange={(e) => setExposure(parseFloat(e.target.value))}
                required
                className="form-control"
                placeholder="Enter USD amount"
              />
            </label>
          </div>
          <div className="form-group">
            <label>
              Number of Simulations
              <input
                type="number"
                value={numSimulations}
                onChange={(e) => setNumSimulations(parseInt(e.target.value, 10))}
                required
                className="form-control"
                placeholder="Enter simulation count"
              />
            </label>
          </div>
          <button 
            type="submit" 
            className={`submit-button ${loading ? 'loading' : ''}`}
            disabled={loading}
          >
            {loading ? `Running Simulation${loadingDots}` : 'Run Simulation'}
          </button>
        </form>

        <div className="results-section">
          {error && <p className="error-message">{error}</p>}
          {jsonData && (
            <>
              <h2 className="results-title">Simulation Results</h2>
              <CombinedHistogram
                width={700}
                height={400}
                unhedged={jsonData.unhedged_revenue}
                option={jsonData.option_revenue}
              />
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default HedgingSimulator;
